import CorrodeBase from './base';
import * as utils from './utils';
import { isPlainObject } from 'lodash';

import * as MAPPERS from './map';
import * as ASSERTIONS from './assert';

import { inspect } from 'util';

/**
 * Corrode
 * A batteries-included library for reading your binary data.
 * It helps you converting that blob-mess into readable data.
 *
 * It inherits from {@link CorrodeBase} to make the abstraction between these
 * two components more obvious. CorrodeBase does all the dirty low-level work
 * reading bytes, managing jobs, etc. This class however doesn't manipulate
 * the {@link CorrodeBase#jobs}-array or messes in other ways with the {@link CorrodeBase#jobLoop}.
 *
 * This class builds on loop and tap to provide more complex functionality.
 */
export default class Corrode extends CorrodeBase {

    /**
     * object holding all the user-defined extensions.
     * these will get bound to the corrode-instance on calling the constructor
     * @type {Object<Function>}
     */
    static EXTENSIONS = {};

    /**
     * mappers-object
     * @type {Object<Function>}
     */
    static MAPPERS = MAPPERS;

    /**
     * assertions-object
     * @type {Object<Function>}
     */
    static ASSERTIONS = ASSERTIONS;

    /**
     * initializes mappers, assertions and extensions
     * @param {object} options {@link CorrodeBase#constructor}
     */
    constructor(options){
        super(...arguments);

        // bind ext, map & assert onto this instance
        /** @type {Object<bound Function>} ext {@link Corrode.EXTENSIONS} bound to this instance */
        this.ext = utils.bindObject(Corrode.EXTENSIONS, this);
        /** @type {Object<bound Function>} map {@link Corrode.MAPPERS} bound to this instance */
        this.map = utils.tapBindObject(Corrode.MAPPERS, this);
        /** @type {Object<bound Function>} assert {@link Corrode.ASSERTIONS} bound to this instance */
        this.assert = utils.tapBindObject(Corrode.ASSERTIONS, this);
    }

    /**
     * pushes a repeat-job onto the job-array
     * a repeat-job repeats itself a given number of times and then ends.
     * it's also possible to end the job prematurely or discard it.
     * This is nothing else than a proxy to {@link CorrodeBase#loop}
     * @param {string} [name] name of the new variable-layer. if none is provided, the current layer will be used
     * @param {number} length iteration-count as number or as string referencing a variable from {@link CorrodeBase#vars}
     * @param {function(end: function, discard: function, i: number)} fn called until `end()` is called.
     *        `end(true)` can be used to end and discard the current loop.
     *        `discard()` can be used to reset the current layer ({@link CorrodeBase#options.anonymousLoopDiscardDeep}).
     *        `i` is the current iteration count
     * @return {Corrode} this
     */
    repeat(name, length, fn){
        if(typeof name === 'number' || typeof length === 'function'){
            fn = length;
            length = name;
            name = undefined;
        }

        return this.tap(function(){
            if(typeof length === 'string'){
                length = this.vars[length];
            }

            if(length === 0){
                if(name){
                    this.vars[name] = [];
                }
                return this;
            }

            const loopGuard = function(end, discard, i){
                fn.call(this, end, discard, i);

                if(i >= length - 1){
                    end();
                }
            };

            if(!name){
                return this.loop(loopGuard);
            }

            return this.loop(name, loopGuard);
        });
    }

    /**
     * pushes a terminatedBuffer-job onto the job-array
     * The returned Buffer will be a slice (not a copy) of the underlying {@link CorrodeBase#streamBuffer}
     * internally this method uses the isSeeking-property to prevent flushing of the
     * underlying buffer.
     * @param {string} name name of the buffer-variable
     * @param {number} [terminator=0] uint8-value indicating the end of the buffer
     * @param {boolean} [discardTerminator=true] whether or not to include the terminator in the resulting buffer
     * @return {Corrode} this
     */
    terminatedBuffer(name, terminator = 0, discardTerminator = true){
        let bufferLength = null;
        const loopVar = Symbol.for('terminatedBufferTmp');

        return this
            .tap(function(){
                terminator = typeof terminator === 'string' ? this.vars[terminator] : terminator;
                /** @type {boolean} isSeeking {@link CorrodeBase#isSeeking} */
                this.isSeeking = true;
            })
            .loop(function(end, discard, i){
                this
                    .uint8(loopVar)
                    .tap(function(){
                        if(this.vars[loopVar] === terminator){
                            bufferLength = i + 1;
                            end();
                        }
                    });
            })
            .tap(function(){
                delete this.vars[loopVar];
                this.vars[name] = this.streamBuffer.slice(this.chunkOffset - bufferLength, this.chunkOffset + (discardTerminator ? -1 : 0));
                this.isSeeking = false;
            });
    }

    /**
     * pushes a terminatedString-job onto the job-array
     * Returns a string ranging from the current offset until the given terminator is found
     * @param {string} name name of the string-variable
     * @param {number} [terminator=0] uint8-value indicating the end of the buffer
     * @param {boolean} [discardTerminator=true] whether or not to include the terminator in the resulting buffer
     * @param {string} [encoding=CorrodeBase#options.encoding] encoding encoding used to decode the string, defaults to 'utf8'.
     *                 available encodings can be found here https://nodejs.org/api/buffer.html#buffer_buffers_and_character_encodings
     * @return {Corrode} this
     */
    terminatedString(name, terminator = 0, discardTerminator = true, encoding = this.options.encoding){
        return this
            .terminatedBuffer(name, terminator, discardTerminator)
            .tap(function(){
                if(!this.vars[name] || this.vars[name].length === 0){
                    // in case nothing is found, return an empty string
                    this.vars[name] = '';
                } else {
                    this.vars[name] = this.vars[name].toString(encoding);
                }
            });
    }

    /**
     * pushes a pointer-job onto the job-array
     * returns an item of an accessable type (object property, array element)
     * being given an accessable type. the offset used to access the accessable type
     * is determined by reading a value of the given type
     * @param {string} name name of the item-variable
     * @param {Object|Array} obj accessable type variable
     * @param {string} type name of the type which should be used to read the index (int8, uint32, etc)
     * @return {Corrode} this
     */
    pointer(name, obj, type = 'int64'){
        return this
            .tap(function(){
                if(typeof obj === 'string'){
                    obj = this.vars[obj];
                }

                this
                    [type](name)
                    .map.abs(name)
                    .map.get(name, obj);
            });
    }

    /**
     * sets {@link CorrodeBase#streamOffset} to a given absolute position
     * like skip, but absolute
     * If you want to set the offset to something lower than the current offset, you should
     * enable {@link CorrodeBase#isSeeking} asap, as otherwise there's no guarantee that the buffer is still available
     * or already flushed.
     * @param {number|string} offset as number or as string referencing a variable from {@link CorrodeBase#vars}
     * @return {Corrode} this
     */
    position(offset){
        return this
            .tap(function(){
                if(typeof offset === 'string'){
                    this.assert.exists(offset);
                    offset = this.vars[offset];
                }

                this.skip(offset - this.streamOffset);
            });
    }

    /**
     * helper utility logging the current vars
     * @return {Corrode} this
     */
    debug(){
        return this.tap(function(){
            console.log(inspect(this.vars, {
                showHidden: false,
                depth: null
            }));
        });
    }

    /**
     * helper utility, parsing a given buffer async
     * @param {Buffer} buffer data
     * @param {function(error: *, data: object)} done callback
     * @return {Corrode} this
     */
    fromBuffer(buffer, done){
        this.end(buffer);
        this.on('finish', () => done(this.vars));
        return this;
    }

    /**
     * adds an extension to corrode.
     * extensions are user-defined functions allowing you to better abstract upon corrode
     * an extension can accept parameters and receives the current available variables.
     * it can either directly return values or read them from the buffer with corrode's functions.
     * @param {string} name of the extension
     * @param {function(...args: *)} fn function receiving arguments given when calling the extension
     * @example <caption>Extension reading values</caption>
     * Corrode.addExtension('foo', function(arg1){ this.uint8(arg1); });
     * (new Corrode()).ext.foo('foo_value', 'arg1');
     * @example <caption>Extension returning values</caption>
     * Corrode.addExtension('bar', function(arg1){ return this.vars[arg1] * this.vars[arg1] });
     * (new Corrode()).ext.bar('bar_value', 'arg1')
     */
    static addExtension(name, fn){
        Corrode.EXTENSIONS[name] = function(name = 'values', ...args){
            return this.tap(name, function(){
                const value = fn.apply(this, args);

                if(typeof value !== 'undefined'){
                    if(this.options.strictObjectMode && this.jobs.length > 0 && value !== this && !isPlainObject(value)){
                        throw new TypeError(`Can't mix immediate returns with later reads on a non-object value (${JSON.stringify(value)}) in strictObjectMode`);
                    }
                    /** @type {Object} vars {@link CorrodeBase#vars} */
                    this.vars = value;
                }
            });
        };

        Corrode.EXTENSIONS[name].orgFn = fn;
    }
}

/**
 * These functions provide basic mapping-abilities for Corrode's VariableStack
 * {@link Corrode#vars}
 *
 * Imagine them like this:
 * ```
 * const parser = new Corrode();
 * parser.uint8('value').map.double('value');
 * ```
 *
 * Of course there's no real mapping-function which doubles a value.
 * But the concept is that they are functions receiving a value, processing it
 * and saving a new value in the {@link VariableStack} in place of the old one.
 *
 * The imaginary code above would yield `{ value: 4 }`, parsing a buffer like this `[2]`.
 *
 * There are two ways to create a mapper. Either by using the {bind} helper-function
 * which simply receives a value and returns one, or by defining the function yourself.
 *
 * The bind-utility only allows for simple functions with no additional parameters.
 * Our double-mapper would be a perfect example: `export const double = bind(val => val * 2)`.
 * These should be pure functions.
 *
 * The other way - defining your own mapper-function accepts deals with the {@link VariableStack}
 * at {@link Corrode#vars} by itself. This means: reads and writes from {@link Corrode#vars}. Because of that
 * they are inherently impure. A next step should be to move all mappers to pure functions.
 * (see Issue #28)
 *
 * Note that all mappers don't check for existance, validity or other assumptions.
 * You have to do that yourself with assertions.
 */

/**
 * helper function to bind a mapper
 * mappers created with this utility accept two parameters:
 * name and src, with the src defaulting to name.
 * This way, we get a mapper which per-default takes the target as the source
 * but also accepts a different source.
 * @param {function(val: *)} fn map-function
 * @return {function}         function ready to use in tap
 */
const bind = function(fn){
    return function(name, src = name){
        this.vars[name] = fn(this.vars[src]);
    };
};

/**
 * replace a variable in the stack by a mapped version of itself
 * @param {string}           name identifier of the variable to map
 * @param {function(val: *)} fn   map-function
 * @example
 * parser.uint8('value').map.callback('value', val => (val - 1) * 2)
 *
 * // [21] => { value: 10 }
 */
export function callback(name, fn){
    this.vars[name] = fn(this.vars[name]);
}

/**
 * retrieve a value from an accessable type (like array[0] or object['foo'])
 * @param {string} name                    identifier of the variable to map
 * @param {array|object|string} accessable accessable variable
 * @param {string} [src=name]              identifier of the variable in {@link Corrode#vars} by which to access `accessable`
 * @example <caption>get from array</caption>
 * parser.uint8('accessor').map.get('accessor', ['A', 'B', 'C', 'D'])
 *
 * // [2] => { accessor: 'C' }
 *
 * @example <caption>get from object</caption>
 * parser.terminatedString('accessor').map.get('accessor', { foo: 'A', bar: 'B', qux: 'C' })
 *
 * // ['q', 'u', 'x', 0x00] => { accessor: 'C' }
 */
export function get(name, accessable, src = name){
    this.vars[name] = accessable[this.vars[src]];
}

/**
 * retrieve a filtered array of objects from an array of objects, matching a specified attribute against a specified value
 * @param {string} name         identifier of the variable, to write to {@link Corrode#vars}
 * @param {Array<Object>} array array, containing the objects to filter
 * @param {string} attr         identifier of the attribute from an object of `array` to compare against
 * @param {string} [src=name]   {@link Corrode#vars}-identifier to read from
 * @throws {Error} when no object can be found
 * @example
 * parser.uint8('matchAgainst').map.findAll('matchAgainst', [
 *   { children: 1, name: 'foo' },
 *   { children: 2, name: 'bar' },
 *   { children: 2, name: 'qux' }
 * ], 'children')
 *
 * // [2] => { matchAgainst: [
 * //   { children: 2, name: 'bar' },
 * //   { children: 2, name: 'qux' }
 * // ]}
 *
 * // [1] => { matchAgainst: [
 * //   { children: 1, name: 'foo' }
 * // ]}
 */
export function findAll(name, array, attr, src = name){
    const filtered = array.filter(item => item[attr] === this.vars[src]);
    if(filtered.length === 0){
        throw new Error(`cannot find object in array with ${attr} === ${src}(${this.vars[src]})`);
    }
    this.vars[name] = filtered;
}

/**
 * retrieve the first object from an array of objects, matching a specified attribute against a specified value
 * like {@link findAll}, but returning only the first element
 * @param {string} name         identifier of the variable, to write to {@link Corrode#vars}
 * @param {Array<Object>} array array, containing the objects to filter
 * @param {string} attr         identifier of the attribute from an object of `array` to compare against
 * @param {string} [src=name]   {@link Corrode#vars}-identifier to read from
 * @throws {Error} when no object can be found
 * @example
 * parser.uint8('matchAgainst').map.find('matchAgainst', [
 *   { id: 1, name: 'foo' },
 *   { id: 7, name: 'bar' },
 *   { id: 4, name: 'qux' }
 * ], 'id')
 *
 * // [4] => { matchAgainst: { id: 4, name: 'qux' } }
 *
 * // [2] => Error cannot find object!
 */
export function find(name, array, attr, src = name){
    findAll.call(this, name, array, attr, src);
    this.vars[name] = this.vars[name][0];
}

/**
 * replace {@link Corrode#vars} completely with a value from {@link Corrode#vars}
 * especially useful when pushing a variable further up in the stack
 *
 * @example <caption>push loop-variables up</caption>
 * parser.loop('array', function(){
 *     this
 *         .uint8('value')
 *         .map.double()
 *         .map.push('value');
 * });
 *
 * // [1, 2, 3, 4] => { array: [2, 4, 6, 8] }
 *
 * @example <caption>push values in an extension</caption>
 * Corrode.addExtension('doStuff', function(){
 *     this
 *         .uint32('address')
 *         .tap(function(){
 *             this.vars.address = `0x${this.vars.address.toString(16)}`;
 *         })
 *         .map.push('address');
 * });
 *
 * parser.ext.doStuff('hexAddress');
 *
 * // [245] => { hexAddress: '0xf5' }
 *
 * @param {string} [name='values'] identifier of the variable being used as replacement
 */
export function push(name = 'values'){
    this.vars = this.vars[name];
}

/**
 * map a value by checking whether it has some bits set
 * @param {string} name    identifier of the variable, to write to {@link Corrode#vars}
 * @param  {Object|number} maskObject Object or number by which to check the bits of the variable to map
 * @example <caption>map via number</caption>
 * parser.uint8('bits').map.bitmask('bits', 0x80)
 *
 * // [0b10111110] => { bits: true }
 *
 * @example <caption>map via object</caption>
 * parser.uint8('bits').map.bitmask('bits', {
 *   isCompressed: 0x80,
 *   isReadOnly: 0x40
 * })
 *
 * // [0b10111110] => { bits: { isCompressed: true, isReadOnly: false } }
 */
export function bitmask(name, maskObject){
    const bits = this.vars[name];

    // shortcut for single values
    if(typeof maskObject === 'number'){
        return this.vars[name] = (bits & maskObject) === maskObject;
    }

    const values = {};
    Object.keys(maskObject).forEach(maskName => {
        const mask = maskObject[maskName];
        values[maskName] = (bits & mask) === mask;
    });
    this.vars[name] = values;
}

/**
 * retrieve absolute value of a number
 * {@link Math.abs}
 * @type {function}
 * @example
 * this.int8('value').map.abs('value')
 *
 * // [-14] => { value: 14 }
 */
export const abs = bind(Math.abs);

/**
 * retrieve inverted number
 * @type {function}
 * @example
 * this.uint8('value').map.abs('value')
 *
 * // [27] => { value: -27 }
 */
export const invert = bind(val => val * -1);

/**
 * retrieve trimmed string
 * @type {function}
 * @example
 * this.terminatedString('value').map.trim('value')
 *
 * // [' ', '\t', 'f', 'o', 'b', 'r', '\n'] => { value: 'fobr' }
 */
export const trim = bind(str => str.trim());

