            .int16le('int16len')
            .int16be('int16ben');

        this.eqFile('int16-seq.bin', done, {
            int16: 2000,
            int16le: 4000,
            int16be: 6000,
            uint16: 34000,
            uint16le: 36000,
            uint16be: 38000,
            int16n: -30000,
            int16len: -31000,
            int16ben: -32000
        });
    });

    /**
     * @test {CorrodeBase#int32}
     * @test {CorrodeBase#int32le}
     * @test {CorrodeBase#int32be}
     * @test {CorrodeBase#uint32}
     * @test {CorrodeBase#uint32le}
     * @test {CorrodeBase#uint32be}
     */
    it('reads int32', function(done){
        this.base
            .int32('int32')
            .int32le('int32le')
            .int32be('int32be')
            .uint32('uint32')
            .uint32le('uint32le')
            .uint32be('uint32be')
            .int32('int32n')
            .int32le('int32len')
            .int32be('int32ben');

        this.eqFile('int32-seq.bin', done, {
            int32: 100000,
            int32le: 110000,
            int32be: 120000,
            uint32: 3000000000,
            uint32le: 3100000000,
            uint32be: 3200000000,
            int32n: -1000000000,
            int32len: -1100000000,
            int32ben: -1200000000
        });
    });

    /**
     * @see http://www.ecma-international.org/ecma-262/5.1/#sec-8.5
     * @test {CorrodeBase#int64}
     * @test {CorrodeBase#int64le}
     * @test {CorrodeBase#int64be}
     * @test {CorrodeBase#uint64}
     * @test {CorrodeBase#uint64le}
     * @test {CorrodeBase#uint64be}
     */
    it('reads int64', function(done){
