            uint64le: 3100000000,
            uint64be: 3200000000,
            int64n: -1000000000,
            int64len: -1100000000,
            int64ben: -1200000000
        });
    });

    /**
     * @test {CorrodeBase#float}
     * @test {CorrodeBase#floatle}
     * @test {CorrodeBase#floatbe}
     */
    it('reads float', function(done){
        this.base
            .float('float')
            .floatle('floatle')
            .floatbe('floatbe')
            .floatle('floatlen')
            .floatbe('floatben');

        // floats are pretty unprecise
        this.eqFile('float-seq.bin', done, vars => {
            expect(vars.float).to.be.within(1.233, 1.235);
            expect(vars.floatle).to.be.within(5.677, 5.679);
            expect(vars.floatbe).to.be.within(9.1010, 9.1012);
