/** @test {CorrodeBase#varStack} */
describe('CorrodeBase#varStack', () => {
    beforeEach(function(){
        this.base = new Base();
        this.eqArray = require('./helpers/asserts').eqArray.bind(this);
    });

    /** @test {CorrodeBase#push} */
    it('pushes and pops', function(done){
        this.base
            .uint8('val_0')
            .push('child')
            .uint8('val_1')
            .push('child')
            .uint8('val_2')
            .pop()
            .uint8('val_3')
            .pop()
            .uint8('val_4');

        this.eqArray([0, 1, 2, 3, 4, 5], done, {
            val_0: 0,
            val_4: 4,
            child: {
                val_1: 1,
                val_3: 3,
                child: {
                    val_2: 2,
                }
            }
        })
    });

    /** @test {CorrodeBase#pop} */
    it('denies popping the root-layer', function(){
        this.base.pop();
        expect(this.eqArray.bind(this, [], () => {}, {})).to.throw(ReferenceError);
    });

    /**
     * @test {CorrodeBase#push}
     * @test {CorrodeBase#pop}
     */
    it('pushes back into old layers', function(done){
        this.base
            .uint8('val_0')
            .push('child')
            .uint8('val_1')
            .pop()
            .uint8('val_2')
            .push('child')
            .uint8('val_3')
            .pop()
            .uint8('val_4');

        this.eqArray([0, 1, 2, 3, 4], done, {
            val_0: 0,
            val_2: 2,
            val_4: 4,
            child: {
                val_1: 1,
                val_3: 3
            }
        });
    });

    /**
     * @test {CorrodeBase#push}
     * @test {CorrodeBase#finishRemainingJobs}
     */
    it('automatically unpushes as it unwinds', function(done){
        this.base
            .uint8('var_0')
            .push('child')
            .uint8('var_1');

        this.eqArray([0, 1, 2], done, {
            var_0: 0,
            child: {
                var_1: 1
            }
        });
    });

    /**
     * @test {CorrodeBase#options.strictObjectMode}
     */
    it('denies pushing into non-object vars', function(){
        this.base
            .uint8('var_0')
            .push('var_0')
            .uint8('var_1');

        expect(this.eqArray.bind(this, [0, 1, 2], () => {}, {})).to.throw(TypeError);
    });
});

const { expect } = require('chai');
const Base = require('../src/base');

/** @test {CorrodeBase#tap} */
describe('CorrodeBase#tap', () => {
    beforeEach(function(){
        this.base = new Base();
        this.eqArray = require('./helpers/asserts').eqArray.bind(this);
    });

    it('taps into the current state', function(done){
        this.base
            .uint8('var_1')
            .tap(function(){
                expect(this.vars.var_1).to.equal(1);
            });

        this.eqArray([1, 2, 3], done, {
            var_1: 1
        });
    });

    it('taps into a named var', function(done){
        this.base
            .uint8('var_1')
            .tap('struct', function(){
                this
                    .uint8('var_2')
                    .uint8('var_3');
            });

        this.eqArray([1, 2, 3], done, {
            var_1: 1,
            struct: {
                var_2: 2,
                var_3: 3
            }
        });
    });

    it('re-taps into existing objects', function(done){
        this.base
            .uint8('var_1')
            .tap('structure', function(){
                this
                    .uint8('var_2')
                    .uint8('var_3')
            })
            .uint8('var_4')
            .tap('structure', function(){
                this
                    .uint8('var_5')
                    .uint8('var_6');
            });

        this.eqArray([1, 2, 3, 4, 5, 6], done, {
            var_1: 1,
            var_4: 4,
            structure: {
                var_2: 2,
                var_3: 3,
                var_5: 5,
                var_6: 6
            }
        });
    });

    it('supports custom arguments in anonymous taps', function(done){
        this.base
            .uint8('var_1')
            .tap(function(subvar_1, subvar_2, subvar_3){
                expect(subvar_1).to.be.true;
                expect(subvar_2).to.be.false;
                expect(subvar_3).to.be.undefined;
                this.uint8('var_3');
            }, [true, false]);

        this.eqArray([1, 2, 3], done, {
            var_1: 1,
            var_3: 2
        });
    });

    it('supports custom arguments in named taps', function(done){
        this.base
            .uint8('var_1')
            .tap('structure', function(subvar_1, subvar_2, subvar_3){
                expect(subvar_1).to.be.true;
                expect(subvar_2).to.be.false;
                expect(subvar_3).to.be.undefined;
                this.uint8('var_3');
            }, [true, false]);

        this.eqArray([1, 2, 3], done, {
            var_1: 1,
            structure: {
                var_3: 2
            }
        });
    });

    /** @test {CorrodeBase#options.strictObjectMode} */
    it('does not allow tapping into other objects', function(){
        this.base
            .uint8('var_1')
            .tap('var_1', function(){
                this.uint8('var_2');
            })
            .uint8('var_3');

        expect(this.eqArray.bind(this, [1, 2, 3], () => {}, {})).to.throw(TypeError);
    });

    /** @test {CorrodeBase#options.strictObjectMode} */
    it('allows tapping into other objects when strictObjectMode is false', function(done){
        this.base = new Base({ strictObjectMode: false });

        this.base
            .uint8('var_1')
            .tap('var_1', function(){
                expect(this.vars).to.be.a.number;
            });

        this.eqArray([1, 2, 3], done, {
            var_1: 1
        });
    });
});

const { expect } = require('chai');
const Corrode = require('../src');

/**
 * @test {Corrode#addExtension}
 * @test {Corrode#ext}
 */
describe('Corrode#ext', () => {
    beforeEach(function(){
        this.base = new Corrode();
        this.eqArray = require('./helpers/asserts').eqArray.bind(this);
    });


    Corrode.addExtension('simpleRepeat', function(arg1, arg2){
        this
            .loop('values', function(end, discard, i){
                if(i >= 3){
                    return end();
                }

                this
                    .uint8(arg1)
                    .uint8(arg2);
            })
            .map.push();
    });


    it('supports extensions', function(done){
        this.base.ext.simpleRepeat('extObj', 'val_1', 'val_2');

        this.eqArray([1, 2, 3, 4, 5, 6], done, {
            extObj: [{
                val_1: 1,
                val_2: 2
            }, {
                val_1: 3,
                val_2: 4
            }, {
                val_1: 5,
                val_2: 6
            }]
        });
    });


    Corrode.addExtension('simpleReturn', function(arg1, arg2){
        return this.varStack.peek()[arg1] + this.varStack.peek()[arg2];
    });

    it('supports extensions with return values and scope-access', function(done){
        this.base
            .uint8('val_1')
            .uint8('val_2')
            .ext.simpleReturn('extReturn', 'val_1', 'val_2');

        this.eqArray([2, 3, 4, 5, 6], done, {
            val_1: 2,
            val_2: 3,
            extReturn: 5
        });
    });


    Corrode.addExtension('mixReturnReadNonObject', function(){
        this.uint8('fix');
        return 4; // chosen by fair dice roll
    });

    it('supports extensions with return and read', function(){
        this.base
            .uint8('var_1')
            .ext.mixReturnReadNonObject('extObj');

        expect(this.eqArray.bind(this, [1, 2, 3], () => {}, {})).to.throw(TypeError);
    });


    Corrode.addExtension('mixReturnReadObject', function(){
        this.uint8('fix');
        return {
            objVal: 'foo'
        };
    });

    it('supports extensions with return and read', function(done){
        this.base
            .uint8('var_1')
            .ext.mixReturnReadObject('extObj');

        this.eqArray([1, 2, 3], done, {
            var_1: 1,
            extObj: {
                fix: 2,
                objVal: 'foo'
            }
        });
    });


    Corrode.addExtension('simpleRead', function(arg1, arg2){
        this
            .uint8(arg1)
            .uint8(arg2)
            .ext.simpleReturn('added', arg1, arg2);
    });

    Corrode.addExtension('callProxy', function(extName, ...args){
        this
            .ext[extName]('values', ...args)
            .map.push();
    });

    it('supports calling extensions within extensions', function(done){
        this.base
            .uint8('var_1')
            .ext.callProxy('proxyVal1', 'simpleRead', 'val_2', 'val_3')
            .ext.callProxy('proxyVal2', 'simpleRead', 'val_4', 'val_5');

        this.eqArray([1, 2, 3, 4, 5, 6], done, {
            var_1: 1,
            proxyVal1: {
                val_2: 2,
                val_3: 3,
                added: 5
            },
            proxyVal2: {
                val_4: 4,
                val_5: 5,
                added: 9
            }
        });
    });
});

